import React, { useState } from "react";
import "./App.css";
import Pageheader from "./Components/Header/Header";

function App() {
  const [activeSection, setActiveSection] = useState("about");

  const handleIconClick = (section) => {
    setActiveSection(section);
  };

  return (
    <div className="App">
      <Pageheader activeSection={activeSection} onIconClick={handleIconClick} />
      <main
        style={{
          display: "flex",
          justifyContent: "center",
          backgroundColor: "#0a192f",
          minHeight: "100vh",
          color: "whitesmoke",
        }}
      >
        <section className={`aboutSection ${activeSection === "about" ? "active" : ""}`}>
          {/* Your about section content */}
        </section>
        <section className={`workSection ${activeSection === "work" ? "active" : ""}`}>
          {/* Your work section content */}
        </section>
        <section className={`experienceSection ${activeSection === "experience" ? "active" : ""}`}>
          {/* Your experience section content */}
        </section>
      </main>
    </div>
  );
}

export default App;
