import React from "react";
import "./HeaderOptions.css";

const HeaderOptions = ({ activeSection, onIconClick, Icon, title }) => {
  const handleClick = () => {
    onIconClick(title.toLowerCase());
  };

  return (
    <div
      className={`header__options ${activeSection === title.toLowerCase() ? "active" : ""}`}
      onClick={handleClick}
    >
      {Icon && <Icon className="headerOptions__icon" />}
      <h3 className="headerOptions__title">{title}</h3>
    </div>
  );
};

export default HeaderOptions;
