import { useCallback, useEffect, useRef } from "react";
import "./App.css";
import Pageheader from "./Components/Header/Header";

function App() {
  const sectionRefs = useRef([]);
  const intObserver = useRef();
  const sectionIntersectionCallback = useCallback((sections) => {
    sections.forEach((section) => {
      if (section.isIntersecting) {
        section.target.classList.add("animate");
      } else {
        section.target.classList.remove("animate"); // Remove the class when not intersecting
      }
    });
  }, []);
  useEffect(() => {
    intObserver.current = new IntersectionObserver(
      sectionIntersectionCallback,
      {
        root: null,
        rootMargin: "0px",
        threshold: 0.5, // Adjust the threshold value as per your requirement
      }
    );

    const sections = document.querySelectorAll(".animate"); // Select sections with the animate class
    sections.forEach((section) => {
      intObserver.current.observe(section);
    });

    return () => {
      intObserver.current.disconnect();
    };
  }, [sectionIntersectionCallback]);

  const handleSection = (sectionId) => {
    const element = sectionRefs.current[sectionId];
    const headerHeight = document.querySelector(".header").offsetHeight; // Get the height of the fixed header
    const sectionTop = element.getBoundingClientRect().top + window.scrollY; // Get the top position of the section relative to the viewport
    const scrolledY = sectionTop - headerHeight; // Calculate the position to scroll to, considering the fixed header height

    window.scrollTo({
      top: scrolledY,
      behavior: "smooth",
    });
  };

  return (
    <div className="App">
      <Pageheader handleSectionClick={handleSection} />
      <main
        style={{
          display: "flex",
          justifyContent: "center",
          backgroundColor: "#0a192f",
          flexDirection: "column",
        }}
      >
        <section
          id="about"
          ref={(el) => (sectionRefs.current["about"] = el)}
          className="animate" // Add animate class to trigger the animation
          style={{
            display: "flex",
            justifyContent: "center",
            backgroundColor: "#0a192f",
            minHeight: "800px",
            color: "whitesmoke",
          }}
        >
          <div>
            <h1>Hi, My name is</h1>
          </div>
          {/* ... */}
        </section>
        <section
          id="work"
          className="animate" // Add animate class to trigger the animation
          style={{
            display: "flex",
            justifyContent: "center",
            backgroundColor: "red",
            minHeight: "800px",
            color: "green",
            transform: "translateX(20px)",
            transition: "opacity 0.5s, transform 1s",
          }}
          ref={(el) => (sectionRefs.current["work"] = el)}
        >
          {/* ... */}
        </section>
        <section
          id="experience"
          className="animate" // Add animate class to trigger the animation
          style={{
            display: "flex",
            justifyContent: "center",
            backgroundColor: "yellow", // Fixed the color here
            minHeight: "800px",
            color: "white",
            transform: "translateX(20px)",
            transition: "opacity 0.5s, transform 1s",
          }}
          ref={(el) => (sectionRefs.current["experience"] = el)}
        >
          {/* ... */}
        </section>
      </main>
    </div>
  );
}

export default App;
