import React from "react";
import "./SideBar.css";
import { IconButton } from "@material-ui/core";
import { Close } from "@material-ui/icons";
const SideBar = (props) => {
  const sidebarStyle = {
    transform: props.show ? "translateX(0)" : "translateX(-100vw)", // Adjusted to -100vw
    opacity: props.show ? "1" : "0",
    transition: "transform 3s ease-out 0s, opacity 4s ease-out 0s", // Adjusted duration
  };
  return (
    <div className="Sidebar" style={sidebarStyle}>
      <div className="top_sidebar">
        <IconButton onClick={props.modalClosed}>
          <Close />
        </IconButton>
      </div>
    </div>
  );
};
export default SideBar;
