import React from "react";
import "./SideBar.css";
import { IconButton } from "@material-ui/core";
import { Close } from "@material-ui/icons";
const SideBar = (props) => {
  /*  const sidebarStyle = {
    transform: props.show ? "translateX(0)" : "translateX(-100vw)", // Adjusted to -100vw
    opacity: props.show ? "1" : "0",
    transition: "1s", // Adjusted duration
  }; */

  const sidebarClasses = ["Sidebar"];
  if (props.show) {
    sidebarClasses.push("active");
  }

  return (
    <div
      className="Sidebar"
      style={{
        transition: " transform 2s ease-in-out, opacity 0.5s ease-in-out",
        opacity: props.show ? "0" : "1",

        transform: props.show ? "translateX(-100%)" : "translateX(0)",
      }}
    >
      <div className="top_sidebar">
        <IconButton onClick={props.modalClosed}>
          <Close />
        </IconButton>
      </div>
    </div>
  );
};
export default SideBar;
